// App.js
import './App.css';
import { ThemeProvider } from '@emotion/react';
import { AppBar, Button, Paper, Toolbar, createTheme, Snackbar, Alert, Box, CircularProgress } from '@mui/material';
import { useState, useEffect } from 'react';
import { Route, Routes, useNavigate } from 'react-router-dom';
import Brightness7Icon from '@mui/icons-material/Brightness7';
import Brightness4Icon from '@mui/icons-material/Brightness4';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import CancelIcon from '@mui/icons-material/Cancel';
import AddMovie from './AddMovie';
import Movie from './Movie';
import AddActor from './AddActor';
import AddProducer from './AddProducer';
import EditMovies from './EditMovies';
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

function App() {
  const [mode, setMode] = useState("dark");
  const navigate = useNavigate();
  const [isApiConnected, setIsApiConnected] = useState(false);
  const [isChecking, setIsChecking] = useState(true);
  const [error, setError] = useState(null);

  const theme = createTheme({
    palette: {
      mode: mode,
    },
  });

  // Check API connectivity
  const checkApiStatus = async () => {
    setIsChecking(true);
    try {
      const movieResponse = await axios.get(`${API_BASE_URL}/movies/health`);
      if (movieResponse.status === 200) {
        setIsApiConnected(true);
        setError(null);
      }
    } catch (err) {
      setIsApiConnected(false);
      setError("Unable to connect to the server. Please try again later.");
      console.error("API Connection Error:", err);
    } finally {
      setIsChecking(false);
    }
  };

  useEffect(() => {
    checkApiStatus();
    const intervalId = setInterval(checkApiStatus, 300000);
    return () => clearInterval(intervalId);
  }, []);

  const handleClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setError(null);
  };

  return (
    <ThemeProvider theme={theme}>
      <Paper className="paper" elevation={5} style={{ borderRadius: "0px", minHeight: "100vh" }}>
        <AppBar className="nav-bar" position="static">
          <Toolbar>
            <Button
              onClick={() => navigate("/")}
              color="inherit"
            >
              <span className='logo'>IMDB</span>
            </Button>
            <Button
              onClick={() => navigate("/")}
              color="inherit"
            >
              All Movies
            </Button>
            <Button
              onClick={() => navigate("/add-movies")}
              color="inherit"
              disabled={!isApiConnected}
            >
              Add Movies
            </Button>
            <Button
              onClick={() => navigate("/add-actor")}
              color="inherit"
              disabled={!isApiConnected}
            >
              Add Actor
            </Button>
            <Button
              onClick={() => navigate("/add-producer")}
              color="inherit"
              disabled={!isApiConnected}
            >
              Add Producer
            </Button>

            {/* Connection Status Button */}
            <Button
              onClick={checkApiStatus}
              style={{
                marginLeft: 'auto',
                marginRight: '10px',
                backgroundColor: isApiConnected ? 'rgba(46, 125, 50, 0.2)' : 'rgba(211, 47, 47, 0.2)',
                color: isApiConnected ? '#2e7d32' : '#d32f2f'
              }}
              startIcon={
                isChecking ? (
                  <CircularProgress size={20} color={isApiConnected ? "success" : "error"} />
                ) : isApiConnected ? (
                  <CheckCircleIcon style={{ color: '#2e7d32' }} />
                ) : (
                  <CancelIcon style={{ color: '#d32f2f' }} />
                )
              }
            >
              {isChecking ? 'Checking...' : isApiConnected ? 'Connected' : 'Disconnected'}
            </Button>

            <Button
              startIcon={mode === 'dark' ? <Brightness7Icon /> : <Brightness4Icon />}
              onClick={() => setMode(mode === "light" ? "dark" : "light")}
              color="inherit"
            >
              {mode === "light" ? "dark" : "light"} mode
            </Button>
          </Toolbar>
        </AppBar>

        {/* Error Message */}
        <Snackbar open={!!error} autoHideDuration={6000} onClose={handleClose}>
          <Alert onClose={handleClose} severity="error">
            {error}
          </Alert>
        </Snackbar>

        <Routes>
          <Route
            exact
            path="/"
            element={<Movie setError={setError} />}
          />
          <Route
            path="/add-movies"
            element={
              <AddMovie setError={setError} />
            }
          />
          <Route
            path="/add-actor"
            element={
              <AddActor setError={setError} />
            }
          />
          <Route
            path="/add-producer"
            element={
              <AddProducer setError={setError} />
            }
          />
          <Route
            path="movies/edit/:id"
            element={
              <EditMovies setError={setError} />
            }
          />
        </Routes>
      </Paper>
    </ThemeProvider>
  );
}

export default App;
