// export default AddMovie;
import React, { useEffect, useState } from "react";
import { useFormik } from "formik";
import * as yup from "yup";
import {
  TextField,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  CircularProgress,
  Box,
} from "@mui/material";
import { useNavigate } from "react-router-dom";
import apiService from "./services/api.service"; // Import apiService

function AddMovie() {
  const navigate = useNavigate();
  const [actors, setActors] = useState([]);
  const [producers, setProducers] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  // Fetch actors and producers on component mount
  useEffect(() => {
    const fetchData = async () => {
      try {
        const actorsData = await apiService.getActors();
        const producersData = await apiService.getProducers();
        setActors(actorsData);
        setProducers(producersData);
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  // Form validation schema
  const movieValidationSchema = yup.object({
    name: yup.string().required("*Name field is mandatory").min(3),
    desc: yup.string().required("*Description is mandatory").min(1),
    director: yup.string().required("*Director field is mandatory").min(5),
    poster: yup.string().required("*Poster is mandatory").min(5),
    yearOfRelease: yup
      .number()
      .required("*Year of release field is mandatory")
      .min(1900, "*Year must be after 1900"),
  });

  // Formik form handling
  const formik = useFormik({
    initialValues: {
      name: "",
      desc: "",
      director: "",
      poster: "",
      yearOfRelease: "",
      producer: "",
      actors: [],
    },
    validationSchema: movieValidationSchema,
    onSubmit: async (values) => {
      try {
        await apiService.addMovie(values);
        alert("Movie added successfully!");
        navigate("/");
      } catch (error) {
        console.error("Error adding movie:", error);
        alert("Failed to add movie. Please try again.");
      }
    },
  });

  // Show loading spinner while fetching data
  if (isLoading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center" }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <form onSubmit={formik.handleSubmit} className="formGroup">
      <TextField
        label="Enter the name"
        variant="outlined"
        id="name"
        name="name"
        value={formik.values.name}
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
        error={formik.touched.name && Boolean(formik.errors.name)}
        helperText={formik.touched.name && formik.errors.name}
      />
      <TextField
        label="Enter the movie Description"
        variant="outlined"
        id="desc"
        name="desc"
        value={formik.values.desc}
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
        error={formik.touched.desc && Boolean(formik.errors.desc)}
        helperText={formik.touched.desc && formik.errors.desc}
      />
      <TextField
        label="Enter the poster link"
        variant="outlined"
        id="poster"
        name="poster"
        value={formik.values.poster}
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
        error={formik.touched.poster && Boolean(formik.errors.poster)}
        helperText={formik.touched.poster && formik.errors.poster}
      />
      <TextField
        label="Enter the director name"
        variant="outlined"
        id="director"
        name="director"
        value={formik.values.director}
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
        error={formik.touched.director && Boolean(formik.errors.director)}
        helperText={formik.touched.director && formik.errors.director}
      />
      <TextField
        label="Enter the year of release"
        variant="outlined"
        id="yearOfRelease"
        name="yearOfRelease"
        value={formik.values.yearOfRelease}
        onChange={formik.handleChange}
        onBlur={formik.handleBlur}
        error={formik.touched.yearOfRelease && Boolean(formik.errors.yearOfRelease)}
        helperText={formik.touched.yearOfRelease && formik.errors.yearOfRelease}
      />
      <FormControl fullWidth>
        <InputLabel id="producer">Producer</InputLabel>
        <Select
          labelId="producer"
          id="producer"
          name="producer"
          value={formik.values.producer}
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          error={formik.touched.producer && Boolean(formik.errors.producer)}
        >
          {producers.map((producer) => (
            <MenuItem key={producer._id} value={producer._id}>
              {producer.name}
            </MenuItem>
          ))}
        </Select>
      </FormControl>
      <FormControl fullWidth>
        <InputLabel id="actors">Actors</InputLabel>
        <Select
          labelId="actors"
          id="actors"
          name="actors"
          multiple
          value={formik.values.actors}
          onChange={formik.handleChange}
          onBlur={formik.handleBlur}
          error={formik.touched.actors && Boolean(formik.errors.actors)}
        >
          {actors.map((actor) => (
            <MenuItem key={actor._id} value={actor._id}>
              {actor.name}
            </MenuItem>
          ))}
        </Select>
      </FormControl>
      <Button variant="contained" type="submit">
        Add Movie
      </Button>
    </form>
  );
}

export default AddMovie;
