import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Loader2 } from "lucide-react";
import { zodResolver } from "@hookform/resolve";
import { useForm } from "react-hook-form";
import * as z from "zod";
import apiService from "./services/api.service";
// import apiService from "./services/api.service"; // Import apiService
// import { API_CONFIG } from "./config/api.config"; // Import API_CONFIG
const movieSchema = z.object({
  name: z.string().min(3, { message: "Name must be at least 3 characters" }),
  desc: z.string().min(1, { message: "Description is required" }),
  director: z.string().min(5, { message: "Director name must be at least 5 characters" }),
  poster: z.string().min(5, { message: "Poster URL must be at least 5 characters" }),
  yearOfRelease: z.number().min(1900, { message: "Invalid year" }),
  producer: z.string().min(1, { message: "Producer is required" }),
  actors: z.array(z.string()).min(1, { message: "At least one actor must be selected" })
});

function EditMovies() {
  const { id } = useParams();
  const [loading, setLoading] = useState(true);
  const [movie, setMovie] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchMovie();
  }, [id]);

  const fetchMovie = async () => {
    try {
      const response = await apiService.get(`/movies/${id}`);
      setMovie(response);
      setLoading(false);
    } catch (err) {
      setError("Failed to fetch movie details");
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <Loader2 className="h-8 w-8 animate-spin" />
      </div>
    );
  }

  if (error) {
    return <div className="text-red-500 text-center">{error}</div>;
  }

  return movie ? <EditMovieForm movie={movie} /> : null;
}

function EditMovieForm({ movie }) {
  const navigate = useNavigate();
  const [producers, setProducers] = useState([]);
  const [actors, setActors] = useState([]);
  const [submitting, setSubmitting] = useState(false);

  const form = useForm({
    resolver: zodResolver(movieSchema),
    defaultValues: {
      name: movie.name,
      desc: movie.desc,
      director: movie.director,
      poster: movie.poster,
      yearOfRelease: movie.yearOfRelease,
      producer: movie.producer,
      actors: movie.actors
    }
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [producersData, actorsData] = await Promise.all([
        apiService.get('/producers'),
        apiService.get('/actors')
      ]);
      setProducers(producersData);
      setActors(actorsData);
    } catch (error) {
      console.error('Failed to fetch data:', error);
    }
  };

  const onSubmit = async (data) => {
    try {
      setSubmitting(true);
      await apiService.post(`/movies/${movie._id}`, data);
      navigate('/');
    } catch (error) {
      console.error('Failed to update movie:', error);
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <Card className="w-full max-w-2xl mx-auto mt-6">
      <CardHeader>
        <CardTitle>Edit Movie</CardTitle>
      </CardHeader>
      <CardContent>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Movie Name</FormLabel>
                  <FormControl>
                    <Input {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="desc"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Description</FormLabel>
                  <FormControl>
                    <Input {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="director"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Director</FormLabel>
                  <FormControl>
                    <Input {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="poster"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Poster URL</FormLabel>
                  <FormControl>
                    <Input {...field} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="yearOfRelease"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Year of Release</FormLabel>
                  <FormControl>
                    <Input type="number" {...field} onChange={(e) => field.onChange(parseInt(e.target.value))} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="producer"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Producer</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger>
                        <SelectValue placeholder="Select a producer" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {producers.map((producer) => (
                        <SelectItem key={producer._id} value={producer._id}>
                          {producer.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="actors"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Actors</FormLabel>
                  <Select
                    onValueChange={(value) => field.onChange([...field.value, value])}
                    value={field.value}
                    multiple
                  >
                    <FormControl>
                      <SelectTrigger>
                        <SelectValue placeholder="Select actors" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      {actors.map((actor) => (
                        <SelectItem key={actor._id} value={actor._id}>
                          {actor.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <Button type="submit" disabled={submitting}>
              {submitting && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
              Update Movie
            </Button>
          </form>
        </Form>
      </CardContent>
    </Card>
  );
}

export default EditMovies;
