// EditMovies.js
import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import CircularProgress from "@mui/material/CircularProgress";
import Box from "@mui/material/Box";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardHeader from "@mui/material/CardHeader";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import { FormControl, InputLabel, MenuItem, Select, FormHelperText } from "@mui/material";
import Snackbar from '@mui/material/Snackbar';
import Alert from '@mui/material/Alert';
import apiService from "./services/api.service";

function EditMovies() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [movie, setMovie] = useState(null);
  const [producers, setProducers] = useState([]);
  const [actors, setActors] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    desc: '',
    director: '',
    poster: '',
    yearOfRelease: '',
    producer: '',
    actors: []
  });
  const [submitting, setSubmitting] = useState(false);
  const [errors, setErrors] = useState({});
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: '',
    severity: 'success'
  });

  useEffect(() => {
    fetchInitialData();
  }, [id]);

  const fetchInitialData = async () => {
    try {
      const [movieData, producersData, actorsData] = await Promise.all([
        apiService.getMovie(id),
        apiService.getProducers(),
        apiService.getActors()
      ]);

      setMovie(movieData);
      setProducers(producersData);
      setActors(actorsData);
      setFormData({
        name: movieData.name || '',
        desc: movieData.desc || '',
        director: movieData.director || '',
        poster: movieData.poster || '',
        yearOfRelease: movieData.yearOfRelease || '',
        producer: movieData.producer || '',
        actors: movieData.actors || []
      });
    } catch (error) {
      setSnackbar({
        open: true,
        message: error.message || 'Failed to load data',
        severity: 'error'
      });
    } finally {
      setLoading(false);
    }
  };

  const validateForm = () => {
    const newErrors = {};
    if (!formData.name || formData.name.length < 3) {
      newErrors.name = "Name must be at least 3 characters";
    }
    if (!formData.desc) {
      newErrors.desc = "Description is required";
    }
    if (!formData.director || formData.director.length < 5) {
      newErrors.director = "Director name must be at least 5 characters";
    }
    if (!formData.poster || formData.poster.length < 5) {
      newErrors.poster = "Poster URL must be at least 5 characters";
    }
    if (!formData.yearOfRelease || formData.yearOfRelease < 1900) {
      newErrors.yearOfRelease = "Invalid year";
    }
    if (!formData.producer) {
      newErrors.producer = "Producer is required";
    }
    if (!formData.actors || formData.actors.length === 0) {
      newErrors.actors = "At least one actor must be selected";
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleActorsChange = (event) => {
    setFormData(prev => ({
      ...prev,
      actors: event.target.value
    }));
  };

  const handleCloseSnackbar = () => {
    setSnackbar(prev => ({ ...prev, open: false }));
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    try {
      setSubmitting(true);
      await apiService.updateMovie(id, formData);
      setSnackbar({
        open: true,
        message: 'Movie updated successfully!',
        severity: 'success'
      });
      setTimeout(() => navigate('/'), 1500);
    } catch (error) {
      setSnackbar({
        open: true,
        message: error.message || 'Failed to update movie',
        severity: 'error'
      });
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: "flex", justifyContent: "center", alignItems: "center", height: "64vh" }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <>
      <Card sx={{ maxWidth: 800, margin: "2rem auto", padding: "1rem" }}>
        <CardHeader title="Edit Movie" />
        <CardContent>
          <form onSubmit={onSubmit} style={{ display: "flex", flexDirection: "column", gap: "1rem" }}>
            <TextField
              label="Movie Name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              error={!!errors.name}
              helperText={errors.name}
              fullWidth
            />

            <TextField
              label="Description"
              name="desc"
              value={formData.desc}
              onChange={handleChange}
              error={!!errors.desc}
              helperText={errors.desc}
              fullWidth
              multiline
              rows={3}
            />

            <TextField
              label="Director"
              name="director"
              value={formData.director}
              onChange={handleChange}
              error={!!errors.director}
              helperText={errors.director}
              fullWidth
            />

            <TextField
              label="Poster URL"
              name="poster"
              value={formData.poster}
              onChange={handleChange}
              error={!!errors.poster}
              helperText={errors.poster}
              fullWidth
            />

            <TextField
              label="Year of Release"
              name="yearOfRelease"
              type="number"
              value={formData.yearOfRelease}
              onChange={handleChange}
              error={!!errors.yearOfRelease}
              helperText={errors.yearOfRelease}
              fullWidth
            />

            <FormControl fullWidth error={!!errors.producer}>
              <InputLabel>Producer</InputLabel>
              <Select
                name="producer"
                value={formData.producer}
                onChange={handleChange}
                label="Producer"
              >
                {producers.map((producer) => (
                  <MenuItem key={producer._id} value={producer._id}>
                    {producer.name}
                  </MenuItem>
                ))}
              </Select>
              {errors.producer && <FormHelperText>{errors.producer}</FormHelperText>}
            </FormControl>

            <FormControl fullWidth error={!!errors.actors}>
              <InputLabel>Actors</InputLabel>
              <Select
                multiple
                name="actors"
                value={formData.actors}
                onChange={handleActorsChange}
                label="Actors"
              >
                {actors.map((actor) => (
                  <MenuItem key={actor._id} value={actor._id}>
                    {actor.name}
                  </MenuItem>
                ))}
              </Select>
              {errors.actors && <FormHelperText>{errors.actors}</FormHelperText>}
            </FormControl>

            <Button 
              type="submit" 
              variant="contained" 
              disabled={submitting}
              sx={{ mt: 2 }}
            >
              {submitting ? "Updating..." : "Update Movie"}
            </Button>
          </form>
        </CardContent>
      </Card>

      <Snackbar 
        open={snackbar.open} 
        autoHideDuration={6000} 
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={handleCloseSnackbar} severity={snackbar.severity} sx={{ width: '100%' }}>
          {snackbar.message}
        </Alert>
      </Snackbar>
    </>
  );
}

export default EditMovies;
