import axios from "axios";
import { API_CONFIG } from "./config/api.config";

const apiClient = axios.create({
  baseURL: API_CONFIG.BASE_URL,
  timeout: API_CONFIG.TIMEOUT,
  headers: {
    "Content-Type": "application/json",
  },
});

apiClient.interceptors.response.use(
  (response) => response,
  async (error) => {
    const originalRequest = error.config;

    if (originalRequest._retry || !shouldRetryError(error)) {
      throw handleApiError(error);
    }

    originalRequest._retry = true;
    await delay(API_CONFIG.RETRY_DELAY * (originalRequest._retryCount || 1));

    return apiClient(originalRequest);
  }
);

const handleApiError = (error) => {
  if (error.response) {
    return new Error(error.response.data?.message || "An error occurred with the server");
  } else if (error.request) {
    return new Error("Unable to connect to the server");
  }
  return new Error("Error setting up the request");
};

const shouldRetryError = (error) => {
  return !error.response || (error.response.status >= 500 && error.response.status <= 599);
};

const delay = (ms) => new Promise((resolve) => setTimeout(resolve, ms));

const apiService = {
  checkHealth: async () => {
    try {
      const response = await apiClient.get(API_CONFIG.ENDPOINTS.HEALTH);
      return response.data;
    } catch (error) {
      throw handleApiError(error);
    }
  },

  checkApiStatus: async (endpoint) => {
    try {
      const response = await apiClient.get(endpoint);
      return response.data;
    } catch (error) {
      throw handleApiError(error);
    }
  },

  getMovies: async () => {
    try {
      const response = await apiClient.get(API_CONFIG.ENDPOINTS.MOVIES);
      return response.data;
    } catch (error) {
      throw handleApiError(error);
    }
  },

  addMovie: async (newMovie) => {
    try {
      const response = await apiClient.post(API_CONFIG.ENDPOINTS.MOVIES, newMovie);
      return response.data;
    } catch (error) {
      throw handleApiError(error);
    }
  },

  getActors: async () => {
    try {
      const response = await apiClient.get(API_CONFIG.ENDPOINTS.ACTORS);
      return response.data;
    } catch (error) {
      throw handleApiError(error);
    }
  },

  getProducers: async () => {
    try {
      const response = await apiClient.get(API_CONFIG.ENDPOINTS.PRODUCERS);
      return response.data;
    } catch (error) {
      throw handleApiError(error);
    }
  },
};

export default apiService;
