// // // // // // //App.js
import './App.css';
import { ThemeProvider } from '@emotion/react';
import { AppBar, Button, Paper, Toolbar, createTheme, Box } from '@mui/material';
import { useState, useEffect } from 'react';
import { Route, Routes, useNavigate } from 'react-router-dom';
import Brightness7Icon from '@mui/icons-material/Brightness7';
import Brightness4Icon from '@mui/icons-material/Brightness4';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import CancelIcon from '@mui/icons-material/Cancel';
import RefreshIcon from '@mui/icons-material/Refresh';
import AddMovie from './AddMovie';
import Movie from './Movie';
import AddActor from './AddActor';
import AddProducer from './AddProducer';
import EditMovies from './EditMovies';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

function App() {
  const [mode, setMode] = useState("dark");
  const navigate = useNavigate();
  const [statuses, setStatuses] = useState({
    backend: false,
    database: false,
    actorApi: false,
    movieApi: false,
    producerApi: false
  });

  const theme = createTheme({
    palette: {
      mode: mode,
    },
  });

  const checkApiStatus = async (endpoint) => {
    try {
      const response = await fetch(`${API_BASE_URL}${endpoint}`);
      return response.ok;
    } catch (error) {
      console.error(`Error checking ${endpoint}:`, error);
      return false;
    }
  };

  const checkAllStatuses = async () => {
    try {
      // Check backend health and database status
      const healthResponse = await fetch(`${API_BASE_URL}/health`);
      const healthData = await healthResponse.json();

      // Check individual API endpoints
      const [actorStatus, movieStatus, producerStatus] = await Promise.all([
        checkApiStatus('/api-status/actor'),
        checkApiStatus('/api-status/movie'),
        checkApiStatus('/api-status/producer')
      ]);

      setStatuses({
        backend: healthResponse.ok,
        database: healthData.database === "connected",
        actorApi: actorStatus,
        movieApi: movieStatus,
        producerApi: producerStatus
      });
    } catch (error) {
      console.error("Status check failed:", error);
      setStatuses({
        backend: false,
        database: false,
        actorApi: false,
        movieApi: false,
        producerApi: false
      });
    }
  };

  useEffect(() => {
    checkAllStatuses();
    const interval = setInterval(checkAllStatuses, 30000);
    return () => clearInterval(interval);
  }, []);

  const StatusIndicator = ({ isOnline, label }) => (
    <Box 
      sx={{ 
        display: 'flex', 
        alignItems: 'center', 
        bgcolor: isOnline ? 'rgba(46, 125, 50, 0.1)' : 'rgba(211, 47, 47, 0.1)',
        color: isOnline ? '#2e7d32' : '#d32f2f',
        py: 0.5,
        px: 2,
        borderRadius: 1,
        mr: 1
      }}
    >
      {isOnline ? (
        <CheckCircleIcon sx={{ fontSize: 20, mr: 1 }} />
      ) : (
        <CancelIcon sx={{ fontSize: 20, mr: 1 }} />
      )}
      {`${label} ${isOnline ? 'ONLINE' : 'OFFLINE'}`}
    </Box>
  );

  return (
    <ThemeProvider theme={theme}>
      <Paper className="paper" elevation={5} style={{ borderRadius: "0px", minHeight: "100vh" }}>
        <AppBar className="nav-bar" position="static">
          <Toolbar>
            <Button
              onClick={() => navigate("/")}
              color="inherit"
            >
              <span className='logo'>IMDB</span>
            </Button>
            <Button
              onClick={() => navigate("/")}
              color="inherit"
            >
              ALL MOVIES
            </Button>
            <Button
              onClick={() => navigate("/add-movies")}
              color="inherit"
            >
              ADD MOVIES
            </Button>
            <Button
              onClick={() => navigate("/add-actor")}
              color="inherit"
            >
              ADD ACTOR
            </Button>
            <Button
              onClick={() => navigate("/add-producer")}
              color="inherit"
            >
              ADD PRODUCER
            </Button>

            <Box sx={{ marginLeft: 'auto', display: 'flex', alignItems: 'center', flexWrap: 'wrap' }}>
              <Box sx={{ display: 'flex', alignItems: 'center', mr: 2 }}>
                <StatusIndicator isOnline={statuses.backend} label="BACKEND" />
                <StatusIndicator isOnline={statuses.database} label="DB" />
                <StatusIndicator isOnline={statuses.actorApi} label="ACTOR" />
                <StatusIndicator isOnline={statuses.movieApi} label="MOVIE" />
                <StatusIndicator isOnline={statuses.producerApi} label="PRODUCER" />
                <Button
                  onClick={checkAllStatuses}
                  color="inherit"
                  size="small"
                  startIcon={<RefreshIcon />}
                >
                  Refresh
                </Button>
              </Box>

              <Button
                onClick={() => setMode(mode === "light" ? "dark" : "light")}
                color="inherit"
                startIcon={mode === 'dark' ? <Brightness7Icon /> : <Brightness4Icon />}
              >
                {mode === "light" ? "DARK" : "LIGHT"} MODE
              </Button>
            </Box>
          </Toolbar>
        </AppBar>

        <Routes>
          <Route exact path="/" element={<Movie />} />
          <Route path="/add-movies" element={<AddMovie />} />
          <Route path="/add-actor" element={<AddActor />} />
          <Route path="/add-producer" element={<AddProducer />} />
          <Route path="movies/edit/:id" element={<EditMovies />} />
        </Routes>
      </Paper>
    </ThemeProvider>
  );
}

export default App;
